/*
 * Artifacts use the "name1" field
 */
#define artifact_p(T) \
        ((T)->name1 ? TRUE : FALSE)

#define true_artifact_p(T) \
        (artifact_p(T) && (T)->name1 != ART_RANDART ? TRUE : FALSE)

/*
 * Ego-Items use the "name2" field
 */
#define ego_item_p(T) \
        ((T)->name2 || (T)->name2b ? TRUE : FALSE)

