#else /* use it for actual stuff.. */
 #define RESF_COND_FORCE	0x00000800ULL	/* force item drop of desired type according to conditions */
 #define RESF_COND_LSWORD	0x00001000ULL	/* force a sword (swordmen, rogues) */
