#else /* NR = +20%..+37% slower than dlvl 127 (200 level_speed base) */
#define level_speed(wpos) \
	((wpos)->wz && !isdungeontown(wpos) ? (getlevel(wpos) <= 127 ? \
	    level_speeds[getlevel(wpos)] * 5 : \
	    (level_speeds[getlevel(wpos)] * (getlevel(wpos) + 60)) / 37) \
	: level_speeds[0] * 5)
